<style scoped lang="sass">
@import "../assets/sass/vars"

.admin

  &-sidebar
    flex-basis: 75px
    flex-shrink: 0
    min-height: 100vh
    background-color: $c-dark
    box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.4)

    &__btn
